// Aktuelles Datum und Suchanfragen vorbereiten
const today = new Date();
const dateStr = today.toISOString().split('T')[0];
const dateDE = today.toLocaleDateString('de-DE', { 
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// 5 verschiedene Suchanfragen fÃ¼r umfassende Analyse
const searches = [
  {
    query: `XAUUSD technical analysis ${dateStr}`,
    type: "search",
    description: "Technische Analyse"
  },
  {
    query: "gold price forecast tomorrow Federal Reserve",
    type: "news",
    description: "News und Forecasts"
  },
  {
    query: "XAUUSD support resistance levels today",
    type: "search",
    description: "Support/Resistance"
  },
  {
    query: "USD index gold correlation today",
    type: "search",
    description: "USD Impact"
  },
  {
    query: "site:tradingview.com OR site:fxstreet.com XAUUSD",
    type: "search",
    description: "Trader Sentiment"
  }
];

return {
  json: {
    date: dateStr,
    dateDE: dateDE,
    timestamp: today.toISOString(),
    searches: searches
  }
};

// Alle Serper-Ergebnisse aggregieren und aufbereiten
const allItems = $input.all();
const allResults = [];
const categories = {};

// Durch alle Items iterieren
for (const item of allItems) {
  const data = item.json;

  // Answer Box (wenn vorhanden)
  if (data.answerBox) {
    allResults.push({
      type: 'answer',
      title: data.answerBox.title || 'Answer',
      content: data.answerBox.snippet || data.answerBox.answer || '',
      source: 'Google Answer Box'
    });
  }

  // Organic Ergebnisse
  if (data.organic && Array.isArray(data.organic)) {
    for (const result of data.organic.slice(0, 5)) {
      allResults.push({
        type: 'organic',
        title: result.title,
        snippet: result.snippet,
        link: result.link,
        date: result.date || 'keine Angabe',
        position: result.position
      });
    }
  }

  // News Ergebnisse
  if (data.news && Array.isArray(data.news)) {
    for (const news of data.news.slice(0, 5)) {
      allResults.push({
        type: 'news',
        title: news.title,
        snippet: news.snippet,
        link: news.link,
        date: news.date || 'keine Angabe',
        source: news.source
      });
    }
  }
}

// Formatiere fÃ¼r AI Agent
const formattedText = allResults.map((result, index) => {
  let text = `\n[${index + 1}] ${result.type.toUpperCase()}: ${result.title}\n`;

  if (result.content) {
    text += `${result.content}\n`;
  }

  if (result.snippet) {
    text += `${result.snippet}\n`;
  }

  if (result.link) {
    text += `Quelle: ${result.link}\n`;
  }

  if (result.date) {
    text += `Datum: ${result.date}\n`;
  }

  text += '---';
  return text;
}).join('\n\n');

const today = new Date().toLocaleDateString('de-DE', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

return {
  json: {
    researchData: formattedText,
    resultCount: allResults.length,
    today: today,
    timestamp: new Date().toISOString(),
    rawResults: allResults
  }
};

